$(document).ready(function () {
  function e() {
    var e = $(".modal__overlay"),
      o = $(".modal__dialog");
    e.addClass("modal__overlay--visible"),
      o.addClass("modal__dialog--visible"),
      (document.querySelector("body").style.overflow = "hidden");
  }
  function o(e) {
    e.preventDefault();
    var r = $(".modal__overlay"),
      a = $(".modal__dialog");
    r.removeClass("modal__overlay--visible"),
      a.removeClass("modal__dialog--visible"),
      (document.querySelector("body").style.overflow = "");
  }
  new Swiper(".hotel-slider", {
    loop: !0,
    navigation: {
      nextEl: ".hotel-slider__button--next",
      prevEl: ".hotel-slider__button--prev",
    },
    keyboard: { enabled: !0, onlyInViewport: !1 },
  }),
    new Swiper(".reviews-slider", {
      loop: !0,
      autoHeight: !0,
      navigation: {
        nextEl: ".reviews-slider__button--next",
        prevEl: ".reviews-slider__button--prev",
      },
    });
  $(".newsletter").parallax({ imageSrc: "img/newsletter-bg.jpeg", speed: 0.3 });
  var r = document.querySelector(".menu-button");
  r.addEventListener("click", function () {
    document
      .querySelector(".navbar-button")
      .classList.toggle("navbar-button--visible");
  });
  var a = $('[data-toggle="modal"]'),
    l = $(".modal__close");
  a.on("click", e),
    l.on("click", o),
    $(document).on("keydown", function (e) {
      "Escape" == e.code && o(e);
    }),
    $(".modal__overlay--close").on("click", function (e) {
      o(e);
    }),
    $(".form").each(function () {
      $(this).validate({
        errorClass: "validate-error",
        messages: {
          name: {
            required: "Please enter your name",
            minlength: jQuery.validator.format(
              "At least 2 characters required!"
            ),
          },
          email: {
            required: "Please enter your email address",
            email: "Format for email address: name@domain.com",
          },
          phone: {
            required: "Please enter your phone number",
            phone: "Format for phone number: +7 (999) 999-99-99",
            minlength: jQuery.validator.format(
              "At least 18 characters required!"
            ),
          },
        },
      }),
        $(".newsletter-subscribe").validate({
          errorClass: "validate-newsletter",
          messages: {
            email: {
              required: "Please enter your email address",
              email: "Format for email address: name@domain.com",
            },
          },
        }),
        $(".phone-number").mask("+7 (999) 999-99-99");
    }),
    AOS.init({
      disable: function () {
        var maxWidth = 992;
        return window.innerWidth < maxWidth;
      },
    });
});
